// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

contract Testing {
    string public info;
    
    function getInfo() public view returns (string memory) {
        return info;
    }

    function setInfo(string memory _info) public {
        info = _info;
    }
}
